import React, { useState } from "react";
import Login from "./Login";
import CreateTodoForm from "./CreateTodoForm";
import TodoList from "./TodoList";
import Navbar from "./Navbar";
import './styles.css';


function App() {
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);

  const handleLogin = (credentials) => {
    setUser(credentials);
  };

  const handleLogout = () => {
    setUser(null);
  };

  const createTodo = (todo) => {
    setTodos([...todos, { ...todo, id: Date.now() }]);
  };

  const updateTodo = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, completed: !todo.completed };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const deleteTodo = (id) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  const handleCloseCreateTodo = () => {
    setShowCreateTodo(false);
    document.querySelector('.navbar').classList.remove('create-todo-mode');
  };
  const [showViewTodos, setShowViewTodos] = useState(false);





    if (!user) {
    return <Login handleLogin={handleLogin} />;
    }

    return (


    <div>
      {/* <header>TO-DO APP</header> */}
    <h1>Welcome, {user.username}!</h1>
    <Navbar
        showCreateTodo={showCreateTodo}
        setShowCreateTodo={setShowCreateTodo}
        showViewTodos={showViewTodos}
        setShowViewTodos={setShowViewTodos}
      />
    <button onClick={handleLogout}>Log Out</button>
    <CreateTodoForm createTodo={createTodo} />
    <TodoList todos={todos} deleteTodo={deleteTodo} updateTodo={updateTodo} />
    </div>
    );
    }

    export default App;
