import React, { useState } from "react";
import Login from "./Login";
import CreateTodoForm from "./CreateTodoForm";
import TodoList from "./TodoList";
import './styles.css';


function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  function handleLogin(event) {
    event.preventDefault();
    if (username === "user" && password === "password") {
      setLoggedIn(true);
    }
  }

  function handleLogout() {
    setLoggedIn(false);
  }

  function handleNewTodoChange(event) {
    setNewTodo(event.target.value);
  }

  function handleNewTodoSubmit(event) {
    event.preventDefault();
    setTodos([...todos, { id: Date.now(), text: newTodo, done: false }]);
    setNewTodo("");
  }

  function handleTodoToggle(todo) {
    setTodos(
      todos.map((t) => {
        if (t.id === todo.id) {
          return { ...t, done: !t.done };
        }
        return t;
      })
    );
  }

  function handleTodoDelete(todo) {
    setTodos(todos.filter((t) => t.id !== todo.id));
  }

  return (
    <div className="container">
      {loggedIn ? (
        <>
          <h1>My Todo List</h1>
          <ul>
            {todos.map((todo) => (
              <li key={todo.id}>
                <input
                  type="checkbox"
                  checked={todo.done}
                  onChange={() => handleTodoToggle(todo)}
                />
                <span className={todo.done ? "done" : ""}>{todo.text}</span>
                <button onClick={() => handleTodoDelete(todo)}>Delete</button>
              </li>
            ))}
          </ul>
          <form className="create-todo-form" onSubmit={handleNewTodoSubmit}>
            <input
              type="text"
              placeholder="New Todo"
              value={newTodo}
              onChange={handleNewTodoChange}
            />
            <button type="submit">Create</button>
          </form>
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <form className="login-form" onSubmit={handleLogin}>
          <h1>Login</h1>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
          <button type="submit">Login</button>
        </form>
      )}
    </div>
  );
}

export default App;
