import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoItem from './TodoItem';

function TodoList() {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    axios.get('/api/todos', { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
      .then(response => {
        setTodos(response.data);
      })
      .catch(() => {
        localStorage.removeItem('token');
        window.location.reload();
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/todos/${id}`, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
      .then(() => {
        setTodos(todos.filter(todo => todo.id !== id));
      })
      .catch(() => {
        localStorage.removeItem('token');
        window.location.reload();
      });
  };

  return (
    <div>
      <h1>Todo List</h1>
      <button onClick={() => window.location.href = '/create'}>Create New Todo</button>
      {todos.map(todo => (
        <TodoItem key={todo.id} todo={todo} handleDelete={handleDelete} />
      ))}
    </div>
  );
}

export default TodoList;
