import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TodoForm(props) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [completed, setCompleted] = useState(false);

  useEffect(() => {
    if (props.match.params.id) {
      axios.get(`/api/todos/${props.match.params.id}`, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
        .then(response => {
          setTitle(response.data.title);
          setDescription(response.data.description);
          setCompleted(response.data.completed);
        })
        .catch(() => {
          localStorage.removeItem('token');
          window.location.href = '/login';
        });
    }
  }, [props.match.params.id]);

  const handleSubmit = (event) => {
    event.preventDefault();

    const todo = {
      title,
      description,
      completed,
    };

    if (props.match.params.id) {
      axios.put(`/api/todos/${props.match.params.id}`, todo, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
        .then(() => {
          window.location.href = '/';
        })
        .catch(() => {
          localStorage.removeItem('token');
          window.location.href = '/login';
        });
    } else {
      axios.post('/api/todos', todo, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
        .then(() => {
          window.location.href = '/';
        })
        .catch(() => {
          localStorage.removeItem('token');
          window.location.href = '/login';
        });
    }
  };

  return (
    <div>
      <h1>{props.match.params.id ? 'Edit' : 'Create'} Todo</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={(event) => setTitle(event.target.value)} />
        </label>
        <br />
        <label>
          Description:
          <textarea value={description} onChange={(event) => setDescription(event.target.value)} />
        </label>
        <br />
        <label>
          Completed:
          <input type="checkbox" checked={completed} onChange={(event) => setCompleted(event.target.checked)} />
        </label>
        <br />
        <button type="submit">{props.match.params.id ? 'Update' : 'Create'}</button>
      </form>
    </div>
  );
}

export default TodoForm;
